package com.example.mvecommerce.repo; import com.example.mvecommerce.domain.Product; import com.example.mvecommerce.domain.Category; import com.example.mvecommerce.domain.Vendor; import java.util.List; import org.springframework.data.jpa.repository.JpaRepository; public interface ProductRepository extends JpaRepository<Product, Long>{ List<Product> findByCategory(Category c); List<Product> findByVendor(Vendor v); List<Product> findByTitleContainingIgnoreCase(String q); }